Scripts.OnEnter:
if ( self.tooltip ) then
	CT_RAMenuHelp_SetTooltip(self);
	GameTooltip:SetText(self.tooltip);
end

Scripts.OnMouseDown:
if ( button == "LeftButton" ) then
	if ( CT_RAMenu_Locked == 0 ) then
		self:GetParent():StartMoving();
	end
elseif ( button == "RightButton" ) then
	self:GetParent():ClearAllPoints();
	self:GetParent():SetPoint("CENTER", "UIParent", "CENTER");
end

Scripts.OnEnter:
if ( CT_RAMenu_Locked == 0 ) then
	CT_RAMenuHelp_SetTooltip(self);
	GameTooltip:SetText("Left-click to drag\nRight-click to reset");
end

Scripts.OnClick:
if ( CT_RAMenu_Locked == 0 ) then
	CT_RAMenu_Locked = 1;
	self:SetText("Unlock");
else
	CT_RAMenu_Locked = 0;
	self:SetText("Lock");
end

Scripts.OnLoad:
_G[self:GetName().."Text"]:SetText("Minimap Button Placement");
_G[self:GetName().."High"]:SetText();
_G[self:GetName().."Low"]:SetText();
self:SetMinMaxValues(0, 360);
self:SetValueStep(1);
self.tooltipText = "Drag the slider to change the position of the minimap button";

Scripts.OnValueChanged:
CT_RASets_ButtonPosition = self:GetValue();
_G[self:GetName().."Text"]:SetText("Minimap Button Placement - "..self:GetValue().."\194\176");
CT_RASets_Button:SetPoint("TOPLEFT", "Minimap", "TOPLEFT", 52 - (80 * cos(self:GetValue())), (80 * sin(self:GetValue())) - 52);

Scripts.OnEnterPressed:
if ( not self.cannotCopy ) then
	CT_RAMenu_CopySet(self:GetParent().from, _G[self:GetParent():GetName() .. "NameEB"]:GetText());
	self:SetText("");
	HideUIPanel(self:GetParent());
end

Scripts.OnTextChanged:
if ( self:GetText() == "" or CT_RAMenu_ExistsSet(self:GetText()) ) then
	_G[self:GetParent():GetName() .. "OkayButton"]:Disable();
	if ( self:GetText() ~= "" ) then
		_G[self:GetParent():GetName() .. "Unavailable"]:Show();
	end
	self.cannotCopy = 1;
else
	_G[self:GetParent():GetName() .. "OkayButton"]:Enable();
	_G[self:GetParent():GetName() .. "Unavailable"]:Hide();
	self.cannotCopy = nil;
end

Scripts.OnShow:
_G[self:GetName() .. "Title"]:SetText(self.type .. " Set");
if ( self.type == "Copy" ) then
	_G[self:GetName() .. "NameEB"]:SetText(self.from .. " Copy");
	_G[self:GetName() .. "NameEB"]:HighlightText();
	if ( CT_RAMenu_ExistsSet(self.from .. " Copy") ) then
		_G[self:GetName() .. "OkayButton"]:Disable();
		_G[self:GetName() .. "Unavailable"]:Show();
		self.cannotCopy = 1;
	else
		_G[self:GetName() .. "OkayButton"]:Enable();
		_G[self:GetName() .. "Unavailable"]:Hide();
		self.cannotCopy = nil;
	end
else
	_G[self:GetName() .. "NameEB"]:SetText("");
end
PlaySound(1115);
