Scripts.OnMouseUp:
if ( button == "LeftButton" ) then
	if ( self.dragall ) then
		CT_RA_DragAllWindows(self, nil);
		self.dragall = nil;
	end
	self:StopMovingOrSizing();
	CT_RAMenu_SaveWindowPositions();
end

Scripts.OnLoad:
self.HPBG = CT_RA_Cache[self].HPBG;
self.MPBG = CT_RA_Cache[self].MPBG;
self.Name = CT_RA_Cache[self].Name;
self.Status = CT_RA_Cache[self].Status;
self.HPBar = CT_RA_Cache[self].HPBar;
self.MPBar = CT_RA_Cache[self].MPBar;
self.Cast = CT_RA_Cache[self].Cast;
self.CastFrame = CT_RA_Cache[self].CastFrame;
self.Percent = CT_RA_Cache[self].Percent;
self.BuffButton1 = CT_RA_Cache[self].BuffButton1;
self.BuffButton2 = CT_RA_Cache[self].BuffButton2;
self.BuffButton3 = CT_RA_Cache[self].BuffButton3;
self.BuffButton4 = CT_RA_Cache[self].BuffButton4;
self.DebuffButton1 = CT_RA_Cache[self].DebuffButton1;
self.DebuffButton2 = CT_RA_Cache[self].DebuffButton2;

self.frameParent = self:GetParent();
self.HPBG:SetVertexColor(0, 1, 0, 0.2);
self.MPBG:SetVertexColor(0, 0, 1, 0.2);
self:SetBackdropColor(0, 0.1, 0.9, 0.5);
self.name = self:GetName();
self.update = 1;
self.HPBar:EnableMouse(false);
self.MPBar:EnableMouse(false);

--self:SetAttribute("type", "target");
ClickCastFrames = ClickCastFrames or { };
ClickCastFrames[self] = true;

Scripts.OnLeave:
if ( SpellIsTargeting() ) then
	SetCursor("CAST_ERROR_CURSOR");
end
CT_RA_CurrentMemberFrame = nil;
GameTooltip:Hide();

Scripts.OnAttributeChanged:
-- OnAttributeChanged(self, name, value)
if (  name == "unit" ) then
	if ( value ) then
		if ( self.frameParent.useModifier ) then
			self.unit = value .. self.frameParent.useModifier;
		else
			self.unit = value;
		end
		self.id = value:match("^raid(%d+)$");
	else
		self.id = nil;
	end
end

Scripts.OnLoad:
self:RegisterEvent("UNIT_HEALTH");
self:RegisterEvent("UNIT_MAXHEALTH");
self:RegisterEvent("UNIT_POWER_UPDATE");
self:RegisterEvent("UNIT_MAXPOWER");
self:RegisterEvent("UNIT_AURA");
self:RegisterEvent("PLAYER_AURAS_CHANGED");

self:RegisterEvent("PLAYER_TARGET_CHANGED");
self:RegisterEvent("PLAYER_ENTERING_WORLD");
self:RegisterEvent("PLAYER_LEAVING_WORLD");
self:RegisterEvent("PLAYER_DEAD");
self:RegisterEvent("PLAYER_ALIVE");

self:RegisterEvent("PLAYER_LOGIN");

self:RegisterEvent("RAID_TARGET_UPDATE");
self:RegisterEvent("GROUP_ROSTER_UPDATE");
self:RegisterEvent("VARIABLES_LOADED");
self:RegisterEvent("UNIT_NAME_UPDATE");

self:RegisterEvent("UNIT_SPELLCAST_SUCCEEDED");
self:RegisterEvent("UPDATE_BINDINGS");
CT_RA_UpdateRaidGroup(3);

self:RegisterEvent("GROUP_ROSTER_UPDATE"); -- "PARTY_MEMBERS_CHANGED"
self:RegisterEvent("SPELLS_CHANGED");
self:RegisterEvent("PLAYER_TALENT_UPDATE");
self:RegisterEvent("CHAT_MSG_SYSTEM");
CT_RA_GetClassSpells();
CT_RA_GetClassTalents();
self.update = 0;
self.updateMT = 0.5;
self.time = 6;
self.updateAFK = 0;
self.mouseOverUpdate = 0;

self:RegisterEvent("CHAT_MSG_RAID");
self:RegisterEvent("CHAT_MSG_RAID_LEADER");
self:RegisterEvent("CHAT_MSG_WHISPER");
self:RegisterEvent("CHAT_MSG_WHISPER_INFORM");
self:RegisterEvent("CHAT_MSG_COMBAT_FRIENDLY_DEATH");
self:RegisterEvent("CHAT_MSG_SYSTEM");
self:RegisterEvent("CHAT_MSG_PARTY");
self:RegisterEvent("CHAT_MSG_ADDON");
self.elapsed = 1;
self.numMessagesSent = 0;
self.flush = 0;

self.HPBar = CT_RA_Cache[self].HPBar;
self.HPBG = CT_RA_Cache[self].HPBG;
self.Name = CT_RA_Cache[self].Name;
self.Deficit = CT_RA_Cache[self].Deficit;
self.Click = CT_RA_Cache[self].Click;
self.ClickFrame = CT_RA_Cache[self.Click].Frame;
self.HPBG:SetVertexColor(0, 1, 0, 0.2);

self.frame1 = CT_RA_Cache[self].Frame1;
self.frame2 = CT_RA_Cache[self].Frame2;
self.frame3 = CT_RA_Cache[self].Frame3;
self.frame4 = CT_RA_Cache[self].Frame4;
self.frame5 = CT_RA_Cache[self].Frame5;
self:SetBackdropColor(0, 0, 0, 0.33);
self:SetBackdropBorderColor(1, 1, 1, 0);

Scripts.OnMouseDown:
if ( button == "LeftButton" and not CT_RAMenu_Options["temp"]["LockEmergency"] ) then
	self:StartMoving();
elseif ( button == "RightButton" and CT_RA_EmergencyFrameTitle:IsVisible() ) then
	CT_RA_Emergency_ToggleDropDown(self);
end

Scripts.OnMouseUp:
if ( button == "LeftButton" ) then
	self:StopMovingOrSizing();
	CT_RAMenu_SaveWindowPositions();
end

Scripts.OnEnter:
CT_RAMenuHelp_SetTooltip(self);
if ( not CT_RAMenu_Options["temp"]["LockEmergency"] ) then
	GameTooltip:SetText("Left-click to drag\nRight-click to open menu");
else
	GameTooltip:SetText("Right-click to open menu");
end

Scripts.OnMouseDown:
if ( button == "LeftButton" and not CT_RAMenu_Options["temp"]["LockMonitor"] ) then
	self:StartMoving();
elseif ( button == "RightButton" ) then
	ToggleDropDownMenu(1, nil, _G[self:GetName() .. "DropDown"], self:GetName(), 47, 15);
end

Scripts.OnShow:
PlaySound(1115);
self.hide = 30;
_G[self:GetName() .. "Info"]:SetText("'|c00FFFFFF" .. CT_RA_CheckReady_Person .. "|r' has requested a ready check.\nPress ready if you are ready.");

Scripts.OnMouseDown:
if ( button == "LeftButton" ) then
	self:GetParent():StartMoving();
elseif ( button == "RightButton" ) then
	self:GetParent():ClearAllPoints();
	self:GetParent():SetPoint("CENTER", "UIParent", "CENTER");
end
